settings.custom_css:
.product-table {
    width: 100%;
    border-collapse: collapse;
}

.table-header {
    background-color: #E2701D; /* Light green background */
    color: white;
    text-align: left;
}

.table-header th {
    padding: 12px; /* Space around header cells */
}

.table-row {
    border-bottom: 1px solid #ddd; /* Light border between rows */
}

.table-row td {
    padding: 8px; /* Space around data cells */
}

.checkbox {
    width: 15px; /* Smaller checkbox */
    height: 15px; /* Smaller checkbox */
}

input[type="number"],
input[type="date"] {
    width: 100%; /* Make input fields take full width */
    padding: 6px; /* Padding for better usability */
    box-sizing: border-box; /* Include padding in width calculation */
}

.table-row:hover {
    background-color: #E2701D; /* Light gray background on hover */
}
